import React, { Component } from 'react';

class Box extends Component {
  render(){

    return (
      <div>
        <div 
          style={{
            backgroundColor: `${this.props.colour}`, 
            width: `${this.props.width}px`, 
            height: `${this.props.height}px`}}/>
        <button>X</button>
      </div>
    );
  }
}

export default Box;
