import React, { Component } from 'react';

import Box from './Box';
import NewBoxForm from './NewBoxForm';

class BoxList extends Component {
  state = {
    boxes: [{colour: null, width: null, height: null}]
  }

  addBoxHandler = (box) => {
    this.setState(state => ({
      boxes: [...state.boxes, box]
    }));
  }

  render(){
    const newBox = this.state.boxes.map(box => <Box colour={this.state.boxes.colour} width={this.state.boxes.width} height={this.state.boxes.height}/>)
    return (
      <div>
        <h1>Colour Box Maker</h1>
        <NewBoxForm addBox={this.addBoxHandler}/>
        {newBox}
      </div>
    )
  }
}

export default BoxList;
