import React, { Component } from 'react';

class ToDo extends Component {
  render(){
    return (
      <div>
        <div>{this.props.task}</div>
        <div><button onClick={this.props.removeToDo}>X</button></div>
      </div>
    )
  }
}

export default ToDo;
