import React, { Component } from 'react';
import axios from 'axios';

const API_URL1 = 'https://deckofcardsapi.com/api/deck/new/shuffle';

class Deck extends Component {
  state = {
    deckId: null,
    card: null
  }

  componentDidMount(){
    axios.get(API_URL1).then(response => {
      this.setState({deckId: response.data.deck_id})
    });
  }

  drawCardHandler = () => {
    const API_URL2 = `https://deckofcardsapi.com/api/deck/${this.state.deckId}/draw/`;

    axios.get(API_URL2).then(response => {
      this.setState({card: response.data.cards.image})
    })
    console.log(card)
  }

  render(){
    return (
      <div>
        <h1>Card Dealer</h1>
        <p>{this.state.deckId}</p>
        <p>{this.state.card}</p>
        <button onClick={this.drawCardHandler}>Draw a Card</button>
      </div>
    )
  }
}

export default Deck;
