import React, { Component } from 'react';

import './Joke.css';

class Joke extends Component {

  colourHandler = () => {
    if (this.props.votes >= 15) {
      return "#4CAF50";
    } else if (this.props.votes >= 11) {
      return "#8BC34A";
    } else if (this.props.votes >= 9) {
      return "#CDDC39";
    } else if (this.props.votes >= 6) {
      return "#FFEB3B";
    } else if (this.props.votes >= 3) {
      return "#FFC107";
    } else if (this.props.votes >= 0) {
      return "#FF9800";
    } else {
      return "#f44336";
    }
  }


  render(){
    return (
      <div className='Joke'>
        <div className='Joke-Buttons'>
          <i className='fas fa-arrow-up' onClick={this.props.upVote}/>
          <i className='fas fa-arrow-down' onClick={this.props.downVote}/>
          <span className='Joke-Votes' style={{borderColor: this.colourHandler()}}>{this.props.votes}</span>
        </div>
        <div className='Joke-Text'>
          {this.props.text}
        </div>
        <div className='Joke-Smiley'>
        <i class={this.props.votes > 10 
          ? "em em-rolling_on_the_floor_laughing" 
          : this.props.votes > 4
          ? "em em-face_with_hand_over_mouth"
          : this.props.votes > 0
          ? "em em-slightly_smiling_face"
          : this.props.votes === 0 
          ? "em em-no_mouth"
          : "em em-no_mouth"
          } aria-role="presentation" aria-label="ROLLING ON THE FLOOR LAUGHING"></i>
        </div>
      </div>
    )
  }
}

export default Joke;
