import React, { Component } from 'react';

import './Joke.css';

class Joke extends Component {
  render(){
    return (
      <div className='Joke'>
        <div className='Joke-Buttons'>
          <i className='fas fa-arrow-up' onClick={this.props.upVote}/>
          <i className='fas fa-arrow-down' onClick={this.props.downVote}/>
          <span className='Joke-Votes'>{this.props.votes}</span>
        </div>
        <div className='Joke-Text'>
          {this.props.text}
        </div>
        <div className='Joke-Smiley'>
        <i class={this.props.votes > 10 
          ? "em em-rolling_on_the_floor_laughing" 
          : this.props.votes > 4
          ? "em em-face_with_hand_over_mouth"
          : this.props.votes === 0 
          ? "em em-neutral_face"
          : "em em-no_mouth"
          } aria-role="presentation" aria-label="ROLLING ON THE FLOOR LAUGHING"></i>
        </div>
      </div>
    )
  }
}

export default Joke;
