import React, { Component } from 'react';
import { Switch, Route } from 'react-router-dom';

import './App.css';
import hopihe from './hopihe.jpg';
import hazel from './hazel.jpg';
import boo from './boo.jpg';
import DogList from './DogList';
import DogDetails from './DogDetails';

class App extends Component {
  static defaultProps = {
    dogs: [
      {
        name: "Hópihe",
        age: 5,
        src: hopihe,
        facts: [
          "Hópihe loves rolling around in snow",
          "Hópihe is a little bit clumsy",
          "Hópihe wants to cuddle with you!"
        ]
      },
      {
        name: "Hazel",
        age: 3,
        src: hazel,
        facts: [
          "Hazel loves dressing up",
          "Hazel is very loyal",
          "Hazel enjoys sleeping. A lot"
        ]
      },
      {
        name: "Boo",
        age: 4,
        src: boo,
        facts: [
          "Boo has soooo much energy!",
          "Boo enjoys walks and playing outdoors",
          "Boo loves eating popcorn"
        ]
      }
    ]
  }

  render() {
    console.log(props.match.params.name);
    const getDog = props => {
      let name = props.match.params.name;
      let currentDog = this.props.dogs.find(
        dog => dog.name.toLowerCase() === name.toLowerCase()
      );
      return <DogDetails {...props} dog={currentDog}/>
    };

    return (
      <Switch>
        <Route path='/dogs' exact render={() => <DogList dogs={this.props.dogs}/>}/>
        <Route path='/dogs/:name' render={() => {getDog()}}/>
      </Switch>
    );
  }
}

export default App;
