import sizes from './sizes';

export default {
  root: {
    backgroundColor: 'navy',
    height: '100vh',
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'center'
  },
  container: {
    width: '60%',
    display: 'flex',
    alignItems: 'flex-start',
    flexDirection: 'column',
    flexWrap: 'wrap',
    [sizes.down('xl')]: {
      width: '80%'
    },
    [sizes.down('xs')]: {
      width: '70%'
    }
  },
  nav: {
    display: 'flex',
    width: '60%',
    justifyContent: 'space-between',
    color: 'white',
    alignItems: 'center',
    "& a": {
      textDecoration: 'none',
      color: 'white',
      border: '3px solid white',
      borderRadius: '20px',
      padding: '10px 15px',
      backgroundColor: 'steelblue'
    }
  },
  palettes: {
    boxSizing: 'border-box',
    width: '100%',
    display: 'grid',
    gridTemplateColumns: 'repeat(4, 25%)',
    gridGap: '2.5rem',
    [sizes.down('md')]: {
      gridTemplateColumns: 'repeat(2, 50%)',
      gridGap: '1.5rem'
    },
    [sizes.down('xs')]: {
      gridTemplateColumns: 'repeat(1, 100%)',
      gridGap: '1rem'
    }
  }
}
