import React, { Component } from 'react';
import classNames from "classnames";
import { withStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Divider from "@material-ui/core/Divider";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import ChevronLeftIcon from "@material-ui/icons/ChevronLeft";
import Fab from '@material-ui/core/Fab';
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';
import { ChromePicker } from 'react-color';

import DraggableColourBox from './DraggableColourBox';

const drawerWidth = 500;

const styles = theme => ({
  root: {
    display: "flex"
  },
  appBar: {
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    })
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen
    })
  },
  menuButton: {
    marginLeft: 12,
    marginRight: 20
  },
  hide: {
    display: "none"
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0
  },
  drawerPaper: {
    width: drawerWidth
  },
  drawerHeader: {
    display: "flex",
    alignItems: "center",
    padding: "0 8px",
    ...theme.mixins.toolbar,
    justifyContent: "flex-end"
  },
  content: {
    flexGrow: 1,
    height: 'calc(100vh - 64px)',
    padding: theme.spacing.unit * 3,
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    }),
    marginLeft: -drawerWidth
  },
  contentShift: {
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen
    }),
    marginLeft: 0
  }
});

class NewPaletteForm extends Component {
  state = {
    open: true,
    currentColour: 'skyblue',
    colourName: '',
    colours: [],
  };

  componentDidMount() {
    ValidatorForm.addValidationRule('isColourNameUnique', value => 
      this.state.colours.every(
        ({name}) => name.toLowerCase() !== value.toLowerCase()
      )
    );
    ValidatorForm.addValidationRule('isColourUnique', value => 
      this.state.colours.every(
        ({colour}) => colour !== this.state.currentColour)
    );
  }

  handleDrawerOpen = () => {
    this.setState({ open: true });
  };

  handleDrawerClose = () => {
    this.setState({ open: false });
  };

  updateCurrentColourHandler = (newColour) => {
    this.setState({ currentColour: newColour.hex});
  }

  addNewColourHandler = () => {
    const newColour = { 
      color: this.state.currentColour,
      name: this.state.colourName }
    this.setState({colours: [...this.state.colours, newColour], newName: ''});
  }

  colourNameHandler = (evt) => {
    this.setState({colourName: evt.target.value});
  }

  savePaletteHandler = () => {
    let newColourName = 'New Test Palette Colour';
    const newPalette = {
      paletteName: newColourName, 
      id: newColourName.toLowerCase().replace(/ /g, '-'), 
      colors: this.state.colours};
    this.props.savePalette(newPalette);
    this.props.history.push('/');
  }

  render() {
    const { classes } = this.props;
    const { open, currentColour, colours } = this.state;

    return (
      <div className={classes.root}>
        <CssBaseline />
        <AppBar
          position='fixed'
          color='default'
          className={classNames(classes.appBar, {
            [classes.appBarShift]: open
          })}
        >
          <Toolbar disableGutters={!open}>
            <IconButton
              color='inherit'
              aria-label='Open drawer'
              onClick={this.handleDrawerOpen}
              className={classNames(classes.menuButton, open && classes.hide)}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant='h6' color='inherit' noWrap>
              Create Your Own Palette
            </Typography>
            <Fab variant='contained' color='primary' onClick={this.savePaletteHandler}>Save Palette</Fab>
          </Toolbar>
        </AppBar>
        <Drawer
          className={classes.drawer}
          variant='persistent'
          anchor='left'
          open={open}
          classes={{
            paper: classes.drawerPaper
          }}
        >
          <div className={classes.drawerHeader}>
            <IconButton onClick={this.handleDrawerClose}>
              <ChevronLeftIcon />
            </IconButton>
          </div>
          <Typography variant='h4'>Design Your Palette</Typography>
          <div>
            <Fab variant='contained' color='secondary'>
              Clear Palette
            </Fab>
            <Fab variant='contained' color='primary'>
              Random Colour
            </Fab>
          </div>
          <Divider />
          <ChromePicker 
            color={currentColour} 
            onChangeComplete={this.updateCurrentColourHandler}/>
          <ValidatorForm onSubmit={this.addNewColourHandler}>
            <TextValidator 
              value={this.state.colourName} 
              onChange={this.colourNameHandler}
              validators={['required', 'isColourUnique', 'isColourNameUnique']}
              errorMessages={['Enter a colour name', 'Colour is already used', 'Colour name must be unique']}
              />
            <Fab 
            variant='contained'
            type='submit' 
            color='primary'
            style={{backgroundColor: currentColour}}
            >
              Add Colour
            </Fab>
          </ValidatorForm>
        </Drawer>
        <main
          className={classNames(classes.content, {
            [classes.contentShift]: open
          })}
        >
          <div className={classes.drawerHeader} />
            {colours.map(color => (
              <DraggableColourBox color={color.color} name={color.name}/>
            ))}
        </main>
      </div>
    );
  }
}

export default withStyles(styles, {withTheme: true})(NewPaletteForm);
