import React, { Component } from 'react';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import Snackbar from '@material-ui/core/Snackbar';
import CloseIcon from '@material-ui/icons/Close';

import './Navbar.css';

class Navbar extends Component {
  state = {
    currentFormat: 'hex',
    open: true
  }

  changeFormatHandler = (e) => {
    this.setState({currentFormat: e.target.value});
    this.props.changeFormatHandler(e.target.value);
  }

  render() {
    const { level, changeLevelHandler } = this.props;
    const { currentFormat } = this.state;

    return (
      <header className='Navbar'>
        <div className='Navbar__Logo'>
          <a href='#'>reactcolourpicker</a>
        </div>
        <div className='Navbar__SliderContainer'>
          <span>Level: {level}</span>
          <div className='Navbar__Slider'>
            <Slider 
              defaultValue={level} 
              min={100} 
              max={900} 
              step={100} 
              onAfterChange={changeLevelHandler}/>
          </div>
        </div>
        <div className='Navbar__SelectContainer'>
          <Select 
            value={currentFormat} 
            onChange={this.changeFormatHandler}>
            <MenuItem value='hex'>HEX - #ffffff</MenuItem>
            <MenuItem value='rgb'>RGB - rgb(255, 255, 255)</MenuItem>
            <MenuItem value='rgba'>RGBA - rgba(255, 255, 255, 1.0)</MenuItem>
          </Select>
        </div>
        <Snackbar 
          anchorOrigin={{vertical: 'bottom', horizontal: 'left'}} 
          open={this.state.open}
          autoHideDuration={3000}
          message={<span>Colour Format Changed to {e.target.value}</span>}/>
      </header>
    );
  }
}

export default Navbar;
