import React, { Component } from 'react';

import './Palette.css';
import Navbar from './Navbar';
import ColourBox from './ColourBox';

class Palette extends Component {
  state = {
    level: 500
  }

  changeLevelHandler = (level) => {
    this.setState({level})
  }

  changeColourCodeHandler = (e) => {
    alert(e.target.value);
  }

  render() {
    const { colors } = this.props.palette;
    const { level } = this.state;

    const colourBoxes = colors[level].map(colour => (
      <ColourBox background={colour.hex} name={colour.name}/>
    ));

    return (
      <div className="Palette">
        <Navbar level={level} changeLevelHandler={this.changeLevelHandler} changeColourCodeHandler={this.changeColourCodeHandler}/>
        <div className='Palette__Colours'>
          {colourBoxes}
        </div>
      </div>
    );
  }
}

export default Palette;
