import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { withStyles } from "@material-ui/core/styles";
import classNames from "classnames";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import Fab from '@material-ui/core/Fab';
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';

styles = theme => ({
  appBar: {
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    })
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen
    })
  },
  menuButton: {
    marginLeft: 12,
    marginRight: 20
  }  
});

class PaletteFormNavbar extends Component {
  state = { newPaletteName: '' }

  componentDidMount() {
    ValidatorForm.addValidationRule("isPaletteNameUnique", value =>
    this.props.palettes.every(
      ({ paletteName }) => paletteName.toLowerCase() !== value.toLowerCase()
      )
    );
  }

  paletteChangeHandler = (evt) => {
    this.setState({
      [evt.target.name]: evt.target.value
    });
  }

  render() {

    const { classes, open, savePaletteHandler, handleDrawerOpen } = this.props;
    const { newPaletteName } = this.state;

    return (
      <div>
        <CssBaseline />
        <AppBar
          position='fixed'
          color='default'
          className={classNames(classes.appBar, {
            [classes.appBarShift]: open
          })}
        >
          <Toolbar disableGutters={!open}>
            <IconButton
              color='inherit'
              aria-label='Open drawer'
              onClick={handleDrawerOpen}
              className={classNames(classes.menuButton, open && classes.hide)}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant='h6' color='inherit' noWrap>
              Create Your Own Palette
            </Typography>
            <ValidatorForm onSubmit={() => savePaletteHandler(newPaletteName)}>
            <TextValidator 
              label='Palette Name' 
              value={newPaletteName}
              name='newPaletteName' 
              onChange={this.paletteChangeHandler}
              validators={['required', 'isPaletteNameUnique']}
              errorMessages={['Enter a palette name', 'Palette name must be unique']}
              />
            <Fab variant='extended' color='primary' type='submit'>Save Palette</Fab>
            <Link to='/'>
              <Fab variant='extended' color='secondary'>Go Back</Fab>
            </Link>
            </ValidatorForm>
          </Toolbar>
        </AppBar>
      </div>
    );
  }
}

export default withStyles(styles, {withTheme: true})(PaletteFormNavbar);
