import React, { Component } from 'react';
import classNames from "classnames";
import { withStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import Typography from "@material-ui/core/Typography";
import Divider from "@material-ui/core/Divider";
import IconButton from "@material-ui/core/IconButton";
import ChevronLeftIcon from "@material-ui/icons/ChevronLeft";
import Fab from '@material-ui/core/Fab';
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';
import { ChromePicker } from 'react-color';
import { arrayMove } from 'react-sortable-hoc';

import PaletteFormNavbar from './PaletteFormNavbar'
import DraggableColourList from './DraggableColorList';

class ColorPickerForm extends Component {
  render() {
    const { paletteIsFull } = this.props;

    return (
      <div>
        <ChromePicker 
            color={currentColor} 
            onChangeComplete={this.updateCurrentColorHandler}/>
          <ValidatorForm onSubmit={this.addNewColorHandler}>
            <TextValidator 
              value={colorName} 
              name='colorName'
              onChange={this.paletteChangeHandler}
              validators={['required', 'isColorUnique', 'isColorNameUnique']}
              errorMessages={['Enter a color name', 'Color is already used', 'Color name must be unique']}
              />
            <Fab 
            variant='extended'
            type='submit' 
            color='primary'
            style={{ backgroundColor: paletteIsFull ? 'grey' : currentColor}}
            disabled={paletteIsFull}
            >
              {paletteIsFull ? 'Palette Full' : 'Add Color'}
            </Fab>
          </ValidatorForm>
      </div>
    );
  }
}

export default ColorPickerForm;
