import chroma from 'chroma-js';

const styles = {
  colorPicker: {
    width: '100% !important',
    marginTop: '2rem'
  },
  addColor: {
    width: '100%',
    padding: '1rem',
    marginTop: '1rem',
    fontSize: '1rem',
    color: props => 
      chroma(props.style).luminance() <= 0.08 
        ? 'rgba(255, 255, 255, 0.8)' 
        : 'rgba(0, 0, 0, 0.6)'
  },
  colorNameInput: {
    width: '100%',
    height: '70px'
  }
}

export default styles;
