import React, { Component } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import { Link } from 'react-router-dom';
import chroma from 'chroma-js';
import { withStyles } from '@material-ui/styles';

import './ColourBox.css';

const styles = {
  copyText: {
    color: props => chroma(props.background).luminance() >= 0.07 ? 'black' : 'white',
    },
  colourNameText: {
    color: props => chroma(props.background).luminance() <= 0.08 ? 'white' : 'black',
  },
  seeMore: {
    color: props => chroma(props.background).luminance() >= 0.07 ? 'rgba(0, 0, 0, 0.5)' : 'white',
    background: 'rgba(255, 255, 255, 0.3)',
    position: 'absolute',
    border: 'none',
    right: '0',
    bottom: '0',
    width: '60px',
    height: '30px',
    textAlign: 'center',
    lineHeight: '30px',
    textTransform: 'uppercase'
  }
};

class ColourBox extends Component {
  state = {
    copied: false
  }

  copyStateHandler = () => {
    this.setState({ copied: true}, () => {
      setTimeout(() => this.setState({copied: false}), 2200);
    });
  }

  render() {
    const { name, background, moreUrl, showLink, classes} = this.props;
    const { copied } = this.state;
    const isDarkColour = chroma(background).luminance() <= 0.08;
    const isLightColour = chroma(background).luminance() >= 0.07;

    return (
        <CopyToClipboard text={background} onCopy={this.copyStateHandler}>
          <div 
            className='ColourBox' 
            style={{ background }}>
            <div 
              className={`ColourBox__CopyOverlay ${copied && 'Show'}`} 
              style={{ background }}/>
            <div className={`ColourBox__CopyMessage ${copied && 'Show'}`}>
              <h1>Copied!</h1>
              <p className={classes.copyText}>
                {background}
              </p>
            </div>
            <div className='ColourBox__CopyContainer'>
              <div className='ColourBox__BoxContent'>
                <span className={classes.colourNameText}>{name}</span>
              </div>
              <button className={`ColourBox__CopyButton ${isLightColour ? 'ColourBox__DarkText' : undefined}`}>COPY</button>  
            </div>
            {showLink && (
              <Link to={moreUrl} onClick={e => e.stopPropagation()}>
                <span className={classes.seeMore}>MORE</span></Link>
            )}
          </div>
        </CopyToClipboard>
    );
  }
}

export default withStyles(styles)(ColourBox);
