import React, { Component } from 'react';
import { withStyles } from '@material-ui/styles';
import DeleteRoundedIcon from '@material-ui/icons/DeleteRounded';

import styles from './styles/MiniPaletteStyles';

class MiniPalette extends Component {
  deletePaletteHandler = (e) => {
    e.stopPropogation();

  }

  render() {
  const { classes, paletteName, emoji, colors, goToPaletteHandler } = this.props;
  const miniColorBoxes = colors.map(color => (
    <div 
      className={classes.miniColor}
      style={{backgroundColor: color.color}}
      key={color.name}
      />
  ));

    return (
      <div className={classes.root} onClick={goToPaletteHandler}>
        <DeleteRoundedIcon 
          className={classes.deleteIcon} 
          style={{transition: 'all 0.3s ease-in-out'}}
          onClick={this.deletePaletteHandler}
          />
        <div className={classes.colors}>
          {miniColorBoxes}
        </div>
        <h5 className={classes.title}>{paletteName}
          <span className={classes.emoji}>{emoji}</span>
        </h5>
      </div>
    );
  }
}

export default withStyles(styles)(MiniPalette);
