import React, { Component } from 'react';

import './Palette.css';
import Navbar from './Navbar';
import ColourBox from './ColourBox';

class Palette extends Component {
  state = {
    level: 500,
    format: 'hex'
  };

  changeLevelHandler = (level) => {
    this.setState({level})
  }

  changeFormatHandler = (val) => {
    this.setState({ format: val });
  }

  render() {
    const { colors, paletteName, emoji } = this.props.palette;
    const { level, format } = this.state;

    const colourBoxes = colors[level].map(colour => (
      <ColourBox background={colour[format]} name={colour.name}/>
    ));

    return (
      <div className="Palette">
        <Navbar 
          level={level} 
          changeLevelHandler={this.changeLevelHandler} 
          changeFormatHandler={this.changeFormatHandler}/>
        <div className='Palette__Colours'>
          {colourBoxes}
        </div>
        <footer class='Palette__Footer'>
          {paletteName}
          <span className='Palette__Emoji'>
            {emoji}
          </span>
        </footer>
      </div>
    );
  }
}

export default Palette;
