import React, { Component } from 'react';
import { withStyles } from '@material-ui/styles';

import './Palette.css';
import Navbar from './Navbar';
import ColourBox from './ColourBox';
import Footer from './Footer';

const styles = {
  Palette: {
    height: '100vh',
    display: 'flex',
    flexDirection: 'column'
  }
}

class Palette extends Component {
  state = {
    level: 500,
    format: 'hex'
  };

  changeLevelHandler = (level) => {
    this.setState({level})
  }

  changeFormatHandler = (val) => {
    this.setState({ format: val });
  }

  render() {
    const { colors, paletteName, emoji, id } = this.props.palette;
    const { classes } = this.props;
    const { level, format } = this.state;

    const colourBoxes = colors[level].map(colour => (
      <ColourBox 
        background={colour[format]} 
        name={colour.name} 
        key={colour.id}
        moreUrl={`/palette/${id}/${colour.id}`}
        showFullPalette/>
    ));

    return (
      <div className={classes.Palette}>
        <Navbar 
          level={level} 
          changeLevelHandler={this.changeLevelHandler} 
          changeFormatHandler={this.changeFormatHandler}
          showingAllColours={true}/>
        <div className='Palette__Colours'>
          {colourBoxes}
        </div>
        <Footer paletteName={paletteName} emoji={emoji}/>
      </div>
    );
  }
}

export default withStyles(styles)(Palette);
