import React, { Component } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import { Link } from 'react-router-dom';
import chroma from 'chroma-js';

import './ColourBox.css';

class ColourBox extends Component {
  state = {
    copied: false
  }

  copyStateHandler = () => {
    this.setState({ copied: true}, () => {
      setTimeout(() => this.setState({copied: false}), 2200);
    });
  }

  render() {
    const {name, background, moreUrl, showLink} = this.props;
    const { copied } = this.state;
    const isDarkColour = chroma(background).luminance() <= 0.08;
    const isLightColour = chroma(background).luminance() >= 0.08;

    return (
        <CopyToClipboard text={background} onCopy={this.copyStateHandler}>
          <div 
            className='ColourBox' 
            style={{ background }}>
            <div 
              className={`ColourBox__CopyOverlay ${copied && 'Show'}`} 
              style={{ background }}/>
            <div className={`ColourBox__CopyMessage ${copied && 'Show'}`}>
              <h1>Copied!</h1>
              <p>{background}</p>
            </div>
            <div className='ColourBox__CopyContainer'>
              <div className='ColourBox__BoxContent'>
                <span className={isDarkColour ? 'ColourBox__LightText' : undefined}></span>
              </div>
              <button className='ColourBox__CopyButton'>COPY</button>  
            </div>
            {showLink && (
              <Link to={moreUrl} onClick={e => e.stopPropagation()}>
                <span className={`ColourBox__SeeMore ${isLightColour ? 'ColourBox__DarkText' : undefined}`}>MORE</span></Link>
            )}
          </div>
        </CopyToClipboard>
    );
  }
}

export default ColourBox;
