import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { withStyles } from "@material-ui/core/styles";
import classNames from "classnames";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import Fab from '@material-ui/core/Fab';
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';

import PaletteMetaForm from './PaletteMetaForm';

const drawerWidth = 500;

const styles = theme => ({
  root: {
    display: 'flex'
  },
  appBar: {
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    })
  },
  flexDirection: 'row',
  justifyContent: 'space-between',
  height: '64px',
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen
    })
  },
  menuButton: {
    marginLeft: 12,
    marginRight: 20
  },
  navButtons: {
    marginRight: '1rem'
  },
  button: {
    margin: '0 0.5rem'
  }  
});

class PaletteFormNavbar extends Component {
  state = { newPaletteName: '', formShowing: false }

  componentDidMount() {
    ValidatorForm.addValidationRule("isPaletteNameUnique", value =>
    this.props.palettes.every(
      ({ paletteName }) => paletteName.toLowerCase() !== value.toLowerCase()
      )
    );
  }

  paletteChangeHandler = (evt) => {
    this.setState({
      [evt.target.name]: evt.target.value
    });
  }

  showFormHandler = () => {
    this.setState({formShowing: true});
  }

  render() {

    const { classes, open, savePaletteHandler, handleDrawerOpen, palettes } = this.props;

    return (
      <div className={classes.root}>
        <CssBaseline />
        <AppBar
          position='fixed'
          color='default'
          className={classNames(classes.appBar, {
            [classes.appBarShift]: open
          })}
        >
          <Toolbar disableGutters={!open}>
            <IconButton
              color='inherit'
              aria-label='Open drawer'
              onClick={handleDrawerOpen}
              className={classNames(classes.menuButton, open && classes.hide)}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant='h6' color='inherit' noWrap>
              Create Your Own Palette
            </Typography>  
            <div className={classes.navButons}>
              <Link to='/'>
                <Fab className={classes.button} variant='extended' color='secondary'>Go Back</Fab>
              </Link>
              <Fab className={classes.button}  variant='extended' color="primary" onClick={this.showFormHandler}>
                Save
              </Fab>
            </div>
          </Toolbar>
        </AppBar>
        {this.state.formShowing && (<PaletteMetaForm savePaletteHandler={savePaletteHandler} palettes={palettes}/>)}
      </div>
    );
  }
}

export default withStyles(styles, {withTheme: true})(PaletteFormNavbar);
