import React, { Component } from 'react';
import { Route, Switch } from 'react-router-dom';

import seedColors from './seedsColors';
import { generatePalette } from './colorHelpers';
import Palette from './Palette';
import PaletteList from './PaletteList';
import SingleColorPalette from './SingleColorPalette';
import NewPaletteForm from './NewPaletteForm';

const savedPalettes = JSON.parse(window.localStorage.getItem('palettes'));

class App extends Component {
  state = {
    palettes: savedPalettes || seedColors
  };

  findPalette = (id) => {
    return this.state.palettes.find(function(palette) {
      return palette.id === id;
    });
  }

  deletePaletteHandler = (id) => {
    this.setState(
      st => ({palettes: st.palettes.filter(palette => palette.id !== id)}),
      this.syncLocalStorageHandler
    )
  }

  savePaletteHandler = (newPalette) => {
    this.setState({palettes: [...this.state.palettes, newPalette]},
      this.syncLocalStorageHandler);
  }

  syncLocalStorageHandler = () => {
    window.localStorage.setItem('palettes', JSON.stringify(this.state.palettes));
  }

  render() {
    return (
      <Switch>
        <Route exact path='/palette/new' render={routeProps => (<NewPaletteForm savePalette={this.savePaletteHandler} {...routeProps} palettes={this.state.palettes}/>)}/>
        <Route exact path='/' render={(routeProps) => <PaletteList palettes={this.state.palettes} deletePaletteHandler={this.deletePaletteHandler} {...routeProps}/>}/>
        <Route exact path='/palette/:id' render={routeProps => (
          <Palette 
            palette={generatePalette(
              this.findPalette(routeProps.match.params.id)
              )}
            />
          )}
        />
        <Route exact path='/palette/:paletteId/:colorId' render={routeProps => (
          <SingleColorPalette 
            colorId={routeProps.match.params.colorId}
            palette={generatePalette(
              this.findPalette(routeProps.match.params.paletteId)
              )}
            />
          )}/>
      </Switch>
    );
  }
}

export default App;
