import React, { Component } from 'react';
import { Route, Switch } from 'react-router-dom';

import seedColours from './seedsColours';
import { generatePalette } from './colourHelpers';
import Palette from './Palette';
import PaletteList from './PaletteList';
import SingleColourPalette from './SingleColourPalette';
import NewPaletteForm from './NewPaletteForm';

class App extends Component {
  state = {
    palettes: seedColours
  };

  findPalette = (id) => {
    return this.state.palettes.find(function(palette) {
      return palette.id === id;
    });
  }

  savePaletteHandler = (newPalette) => {
    this.setState({palettes: [...this.state.palettes, newPalette]});
  }

  render() {
    return (
      <Switch>
        <Route exact path='/palette/new' render={routeProps => (<NewPaletteForm savePalette={this.savePaletteHandler} {...routeProps}/>)}/>
        <Route exact path='/' render={(routeProps) => <PaletteList palettes={this.state.palettes} {...routeProps}/>}/>
        <Route exact path='/palette/:id' render={routeProps => (
          <Palette 
            palette={generatePalette(
              this.findPalette(routeProps.match.params.id)
              )}
            />
          )}
        />
        <Route exact path='/palette/:paletteId/:colourId' render={routeProps => (
          <SingleColourPalette 
            colourId={routeProps.match.params.colourId}
            palette={generatePalette(
              this.findPalette(routeProps.match.params.paletteId)
              )}
            />
          )}/>
      </Switch>
    );
  }
}

export default App;
