import React from 'react';
import { withStyles } from '@material-ui/styles';

import styles from './styles/MiniPaletteStyles';

function MiniPalette(props){
  const { classes, paletteName, emoji, colors, goToPaletteHandler } = props;
  const miniColourBoxes = colors.map(colour => (
    <div 
      className={classes.miniColour}
      style={{backgroundColor: colour.color}}
      key={colour.name}
      />
  ));

  return (
    <div className={classes.root} onClick={goToPaletteHandler}>
      <div className={classes.colours}>
        {miniColourBoxes}
      </div>
      <h5 className={classes.title}>{paletteName}
        <span className={classes.emoji}>{emoji}</span>
      </h5>
    </div>
  );
}

export default withStyles(styles)(MiniPalette);
