import React, { Component } from 'react';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css';

import ColourBox from './ColourBox';
import './Palette.css';

class Palette extends Component {
  state = {
    level: 500
  }

  changeLevelHandler = (level) => {
    this.setState({level})
  }

  render() {
    const colourBoxes = this.props.palette.colors[this.state.level].map(colour => (
      <ColourBox background={colour.hex} name={colour.name}/>
    ));

    return (
      <div className="Palette">
        <Slider defaultValue={this.state.level} min={100} max={900} onAfterChange={this.changeLevelHandler}/>
        <div className='Palette__Colours'>
          {colourBoxes}
        </div>
      </div>
    );
  }
}

export default Palette;
