import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { withStyles } from '@material-ui/styles';
import { CSSTransition, TransitionGroup } from "react-transition-group";
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import CheckIcon from '@material-ui/icons/Check'

import styles from './styles/PaletteListStyles';
import MiniPalette from './MiniPalette';

class PaletteList extends Component {
  goToPaletteHandler = (id) => {
    this.props.history.push(`/palette/${id}`)
  }

  render() {
    const { palettes, classes, deletePaletteHandler } = this.props;

    return (
      <div className={classes.root}>
        <div className={classes.container}>
          <nav className={classes.nav}>
            <h1 className={classes.heading}>React Colours</h1>
            <Link to="/palette/new">Create Palette</Link>
          </nav>
            <TransitionGroup className={classes.palettes}>
              {palettes.map(palette => (
                <CSSTransition
                  key={palette.id}
                  classNames='fade'
                  timeout={500}
                  >
                  <MiniPalette 
                    {...palette} 
                    key={palette.id}
                    id={palette.id}
                    goToPaletteHandler={() => this.goToPaletteHandler(palette.id)} 
                    deletePaletteHandler={deletePaletteHandler}/>
                </CSSTransition>
              ))}
            </TransitionGroup>
        </div>
        <Dialog open={true}>
          <DialogTitle>Are you sure you want to delete this palette?</DialogTitle>
          <List>
            <ListItem>
              <ListItemAvatar>

              </ListItemAvatar>
            </ListItem>
          </List>
        </Dialog>
      </div>
    );
  }
}

export default withStyles(styles)(PaletteList);
