import React, { Component } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import { Link } from 'react-router-dom';
import chroma from 'chroma-js';
import { withStyles } from '@material-ui/styles';

import './ColourBox.css';

const styles = {
  colourBox: {
    width: '20%',
    height: props => props.showFullPalette ? '25%' : '50%',
    margin: '0 auto',
    display: 'inline-block',
    position: 'relative',
    cursor: 'pointer',
    marginBottom: '-3.5px',
    '&:hover button' : {
      opacity: '1',
    }
  },
  copyText: {
    color: props => chroma(props.background).luminance() >= 0.07 ? 'black' : 'white',
    },
  colourNameText: {
    color: props => chroma(props.background).luminance() <= 0.08 ? 'white' : 'black',
  },
  seeMore: {
    color: props => chroma(props.background).luminance() >= 0.07 ? 'rgba(0, 0, 0, 0.5)' : 'white',
    background: 'rgba(255, 255, 255, 0.3)',
    position: 'absolute',
    border: 'none',
    right: '0',
    bottom: '0',
    width: '60px',
    height: '30px',
    textAlign: 'center',
    lineHeight: '30px',
    textTransform: 'uppercase'
  },
  copyButton: {
    color: props => chroma(props.background).luminance() >= 0.07 ? 'rgba(0, 0, 0, 0.5)' : 'white',
    width: '100px',
    height: '30px',
    position: 'absolute',
    display: 'inline-block',
    top: '50%',
    left: '50%',
    marginLeft: '-50px',
    marginTop: '-15px',
    textAlign: 'center',
    outline: 'none',
    background: 'rgba(255, 255, 255, 0.3)',
    fontSize: '1rem',
    lineHeight: '30px',
    textTransform: 'uppercase',
    border: 'none',
    cursor: 'pointer',
    textDecoration: 'none',
    opacity: '0'
  },
  boxContent: {
    position: 'absolute',
    padding: '10px',
    width: '100%',
    left: '0',
    bottom: '0',
    color: 'black',
    letterSpacing: '1px',
    textTransform: 'uppercase',
    fontSize: '12px',
  },
  copyOverlay: {
    opacity: '0',
    zIndex: '0',
    width: '100%',
    height: '100%',
    transition: 'transform 0.6s ease-in-out',
    transition: 'scale(0.1)'
  }
};

class ColourBox extends Component {
  state = {
    copied: false
  }

  copyStateHandler = () => {
    this.setState({ copied: true}, () => {
      setTimeout(() => this.setState({copied: false}), 2200);
    });
  }

  render() {
    const { name, background, moreUrl, showFullPalette, classes} = this.props;
    const { copied } = this.state;

    return (
        <CopyToClipboard text={background} onCopy={this.copyStateHandler}>
          <div 
            className={classes.colourBox}
            style={{ background }}>
            <div 
              className={`${copyOverlay} ${copied && 'Show'}`} 
              style={{ background }}/>
            <div className={`ColourBox__CopyMessage ${copied && 'Show'}`}>
              <h1>Copied!</h1>
              <p className={classes.copyText}>
                {background}
              </p>
            </div>
            <div>
              <div className={classes.boxContent}>
                <span className={classes.colourNameText}>{name}</span>
              </div>
              <button className={classes.copyButton}>COPY</button>  
            </div>
            {showFullPalette && (
              <Link to={moreUrl} onClick={e => e.stopPropagation()}>
                <span className={classes.seeMore}>MORE</span></Link>
            )}
          </div>
        </CopyToClipboard>
    );
  }
}

export default withStyles(styles)(ColourBox);
