import React, { Component } from 'react';
import { Link } from 'react-router-dom';

import Navbar from './Navbar';
import ColourBox from './ColourBox';
import Footer from './Footer';
import { withStyles } from '@material-ui/styles';

const styles = {
  Palette: {
    height: '100vh',
    display: 'flex',
    flexDirection: 'column'
  },
  PaletteColours: {
    height: '90%'
  },
  GoBackButton: {
    width: '20%',
    height: '50%',
    margin: '0 auto',
    display: 'inline-block',
    position: 'relative',
    cursor: 'pointer',
    marginBottom: '-3.5px',
    opacity: 1,
    backgroundColor: 'black',
    '& a': {
      color: 'white',
      width: '100px',
      height: '30px',
      position: 'absolute',
      display: 'inline-block',
      top: '50%',
      left: '50%',
      marginLeft: '-50px',
      marginTop: '-15px',
      textAlign: 'center',
      outline: 'none',
      background: 'rgba(255, 255, 255, 0.3)',
      fontSize: '1rem',
      lineHeight: '30px',
      textTransform: 'uppercase',
      border: 'none',
      cursor: 'pointer',
      textDecoration: 'none',
    }
  }
};

class SingleColourPalette extends Component {
  colourShadesHandler = (palette, colourToFilterBy) => {
    let shades = [];
    let allColours = palette.colors;

    for (let key in allColours) {
      shades = shades.concat(
        allColours[key].filter(colour => colour.id === colourToFilterBy)
      );
    }
    return shades.slice(1);
  }

  state = {
    shades: this.colourShadesHandler(this.props.palette, this.props.colourId),
    format: 'hex'
  }

  changeFormatHandler = (val) => {
    this.setState({ format: val });
  }

  render() {
    const { format, shades } = this.state;
    const { paletteName, emoji, id } = this.props.palette;
    const { classes } = this.props;

    const colourBoxes = shades.map(colour => (
      <ColourBox key={colour.name} name={colour.name} background={colour[format]} showFullPalette={false}/>
    ))
    return (
      <div className={classes.Palette}>
        <Navbar changeFormatHandler={this.changeFormatHandler} showingAllColours={false}/>
        <h1>SingleColourPalette</h1>
        <div className={classes.PaletteColours}>
          {colourBoxes}
          <div className={classes.GoBackButton}>
            <Link to={`/palette/${id}`}>GO BACK</Link>
          </div>
        </div>
        <Footer paletteName={paletteName} emoji={emoji}/>
      </div>
    );
  }
}

export default withStyles(styles)(SingleColourPalette);
