import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { withStyles } from '@material-ui/styles';
import { CSSTransition, TransitionGroup } from "react-transition-group";
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import CheckIcon from '@material-ui/icons/Check';
import CloseIcon from '@material-ui/icons/Close';
import blue from '@material-ui/core/colors/blue';
import red from '@material-ui/core/colors/red';

import styles from './styles/PaletteListStyles';
import MiniPalette from './MiniPalette';

class PaletteList extends Component {
  goToPaletteHandler = (id) => {
    this.props.history.push(`/palette/${id}`)
  }

  render() {
    const { palettes, classes, deletePaletteHandler } = this.props;

    return (
      <div className={classes.root}>
        <div className={classes.container}>
          <nav className={classes.nav}>
            <h1 className={classes.heading}>React Colours</h1>
            <Link to="/palette/new">Create Palette</Link>
          </nav>
            <TransitionGroup className={classes.palettes}>
              {palettes.map(palette => (
                <CSSTransition
                  key={palette.id}
                  classNames='fade'
                  timeout={500}
                  >
                  <MiniPalette 
                    {...palette} 
                    key={palette.id}
                    id={palette.id}
                    goToPaletteHandler={() => this.goToPaletteHandler(palette.id)} 
                    deletePaletteHandler={deletePaletteHandler}/>
                </CSSTransition>
              ))}
            </TransitionGroup>
        </div>
        <Dialog open={true}>
          <DialogTitle>Are you sure you want to delete this palette?</DialogTitle>
          <DialogContent>'Cause once it's gone it's gone :(</DialogContent>
          <List>
            <ListItem>
              <ListItemAvatar>
                <Avatar style={{backgroundColor: blue[100], color: blue[600]}}>
                  <CheckIcon/>
                </Avatar>
              </ListItemAvatar>
              <ListItemText primary='Delete'/>
            </ListItem>
            <ListItem>
              <ListItemAvatar>
                <Avatar style={{backgroundColor: red[100], color: red[600]}}>
                    <CloseIcon/>
                  </Avatar>
                </ListItemAvatar>
                <ListItemText primary='Cancel'/>
            </ListItem>
          </List>
        </Dialog>
      </div>
    );
  }
}

export default withStyles(styles)(PaletteList);
