import React, { Component } from 'react';
import classNames from "classnames";
import { withStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import Typography from "@material-ui/core/Typography";
import Divider from "@material-ui/core/Divider";
import IconButton from "@material-ui/core/IconButton";
import ChevronLeftIcon from "@material-ui/icons/ChevronLeft";
import Fab from '@material-ui/core/Fab';
import { arrayMove } from 'react-sortable-hoc';

import PaletteFormNavbar from './PaletteFormNavbar';
import ColorPickerForm from './ColorPickerForm';
import DraggableColourList from './DraggableColorList';

const drawerWidth = 500;

const styles = theme => ({
  root: {
    display: "flex"
  },
  hide: {
    display: "none"
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0
  },
  drawerPaper: {
    width: drawerWidth
  },
  drawerHeader: {
    display: "flex",
    alignItems: "center",
    padding: "0 8px",
    ...theme.mixins.toolbar,
    justifyContent: "flex-end"
  },
  content: {
    flexGrow: 1,
    height: 'calc(100vh - 64px)',
    padding: theme.spacing.unit * 3,
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    }),
    marginLeft: -drawerWidth
  },
  contentShift: {
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen
    }),
    marginLeft: 0
  },
  container: {
    width: '90%',
    display: 'flex',
    flexDirection: 'column'
  },
  button: {

  }
});

class NewPaletteForm extends Component {
  static defaultProps = {
    maxColors: 20
  }

  state = {
    open: true,
    colors: this.props.palettes[0].colors,
    newPaletteName: '',
  };

  handleDrawerOpen = () => {
    this.setState({ open: true });
  };

  handleDrawerClose = () => {
    this.setState({ open: false });
  };

  addNewColorHandler = (newColor) => {
    this.setState({colors: [...this.state.colors, newColor], newName: ''});
  }

  paletteChangeHandler = (evt) => {
    this.setState({
      [evt.target.name]: evt.target.value
    });
  }

  savePaletteHandler = (newPaletteName) => {
    const newPalette = {
      paletteName: newPaletteName, 
      id: newPaletteName.toLowerCase().replace(/ /g, '-'), 
      colors: this.state.colors};
    this.props.savePalette(newPalette);
    this.props.history.push('/');
  }

  deleteColorHandler = (colorName) => {
    this.setState({
      colors: this.state.colors.filter(color => color.name !== colorName)
    });
  }

  sortEndHandler = ({oldIndex, newIndex}) => {
    this.setState(({colors}) => ({
      colors: arrayMove(colors, oldIndex, newIndex),
    }));
  }

  clearPaletteHandler = () => {
    this.setState({colors: []});
  }

  randomColorHandler = () => {
    const allColors = this.props.palettes.map(p => p.colors).flat();
    var randomNumber = Math.floor(Math.random() * allColors.length);
    const randomColor = allColors[randomNumber];
    this.setState({colors: [...this.state.colors, randomColor]});
  }

  render() {
    const { classes, maxColors, palettes } = this.props;
    const { open, colors } = this.state;
    const paletteIsFull = colors.length >= maxColors;

    return (
      <div className={classes.root}>
        <PaletteFormNavbar 
          open={open}
          palettes={palettes} 
          savePaletteHandler={this.savePaletteHandler} 
          handleDrawerOpen={this.handleDrawerOpen}/>
        <Drawer
          className={classes.drawer}
          variant='persistent'
          anchor='left'
          open={open}
          classes={{
            paper: classes.drawerPaper
          }}
        >
          <div className={classes.drawerHeader}>
            <IconButton onClick={this.handleDrawerClose}>
              <ChevronLeftIcon />
            </IconButton>
          </div>
          <div className={classes.container}>
            <Typography variant='h4'>Design Your Palette</Typography>
            <div className={classes.buttons}>
              <Fab variant='extended' color='secondary' onClick={this.clearPaletteHandler}>
                Clear Palette
              </Fab>
              <Fab variant='extended' color='primary' onClick={this.randomColorHandler} disabled={paletteIsFull}>
              {paletteIsFull ? 'Palette Full' : 'Random Color'}
              </Fab>
            </div>
            <Divider />
            <ColorPickerForm paletteIsFull={paletteIsFull} addNewColorHandler={this.addNewColorHandler} colors={colors}/>
          </div>
        </Drawer>
        <main
          className={classNames(classes.content, {
            [classes.contentShift]: open
          })}
        >
          <div className={classes.drawerHeader} />
          <DraggableColourList 
            colors={colors} 
            deleteColorHandler={this.deleteColorHandler}
            axis='xy'
            onSortEnd={this.sortEndHandler}
            />
        </main>
      </div>
    );
  }
}

export default withStyles(styles, {withTheme: true})(NewPaletteForm);
