import React, { Component } from 'react';
import { Route, Switch } from 'react-router-dom';

import seedColours from './seedsColours';
import { generatePalette } from './colourHelpers';
import Palette from './Palette';
import PaletteList from './PaletteList';
import SingleColourPalette from './SingleColourPalette';

class App extends Component {
  findPalette(id) {
    return seedColours.find(function(palette) {
      return palette.id === id;
    });
  }

  render() {
    return (
      <Switch>
        <Route exact path='/palette/new' render={() => <h1>new palette form</h1>}/>
        <Route exact path='/' render={(routeProps) => <PaletteList palettes={seedColours} {...routeProps}/>}/>
        <Route exact path='/palette/:id' render={routeProps => (
          <Palette 
            palette={generatePalette(
              this.findPalette(routeProps.match.params.id)
              )}
            />
          )}
        />
        <Route exact path='/palette/:paletteId/:colourId' render={routeProps => (
          <SingleColourPalette 
            colourId={routeProps.match.params.colourId}
            palette={generatePalette(
              this.findPalette(routeProps.match.params.paletteId)
              )}
            />
          )}/>
      </Switch>
    );
  }
}

export default App;
