import React, { Component } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';

import './ColourBox.css';

class ColourBox extends Component {
  state = {
    copied: false
  }

  copyStateHandler = () => {
    this.setState({ copied: true}, () => {
      setTimeout(() => this.setState({copied: false}), 2500);
    });
  }

  render() {
    const {name, background} = this.props;
    const { copied } = this.state;

    return (
        <CopyToClipboard text={background} onCopy={this.copyStateHandler}>
          <div 
            className='ColourBox' 
            style={{ background }}>
            <div 
              className={`ColourBox__CopyOverlay ${copied && 'Show'}`} 
              style={{ background }}/>
            <div className={`ColourBox__CopyMessage ${copied && 'Show'}`}>
              <h1>Copied!</h1>
              <p>{background}</p>
            </div>
            <div className='ColourBox__CopyContainer'>
              <div className='ColourBox__BoxContent'>
                <span>{name}</span>
              </div>
              <button className='ColourBox__CopyButton'>COPY</button>  
            </div>
            <span className='ColourBox__SeeMore'>MORE</span>
          </div>
        </CopyToClipboard>
    );
  }
}

export default ColourBox;
