import React, { Component } from 'react';
import classNames from "classnames";
import { withStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Divider from "@material-ui/core/Divider";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import ChevronLeftIcon from "@material-ui/icons/ChevronLeft";
import Fab from '@material-ui/core/Fab';
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';
import { ChromePicker } from 'react-color';

import DraggableColorBox from './DraggableColorBox';
import DraggableColourList from './DraggableColorList';

const drawerWidth = 500;

const styles = theme => ({
  root: {
    display: "flex"
  },
  appBar: {
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    })
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen
    })
  },
  menuButton: {
    marginLeft: 12,
    marginRight: 20
  },
  hide: {
    display: "none"
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0
  },
  drawerPaper: {
    width: drawerWidth
  },
  drawerHeader: {
    display: "flex",
    alignItems: "center",
    padding: "0 8px",
    ...theme.mixins.toolbar,
    justifyContent: "flex-end"
  },
  content: {
    flexGrow: 1,
    height: 'calc(100vh - 64px)',
    padding: theme.spacing.unit * 3,
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    }),
    marginLeft: -drawerWidth
  },
  contentShift: {
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen
    }),
    marginLeft: 0
  }
});

class NewPaletteForm extends Component {
  state = {
    open: true,
    currentColor: 'skyblue',
    colorName: '',
    colors: ['red'],
    newPaletteName: '',
  };

  componentDidMount() {
    ValidatorForm.addValidationRule('isColorNameUnique', value => 
      this.state.colors.every(
        ({name}) => name.toLowerCase() !== value.toLowerCase()
      )
    );
    ValidatorForm.addValidationRule('isColorUnique', value => 
      this.state.colors.every(
        ({color}) => color !== this.state.currentColor)
    );
    ValidatorForm.addValidationRule("isPaletteNameUnique", value =>
    this.props.palettes.every(
      ({ paletteName }) => paletteName.toLowerCase() !== value.toLowerCase()
    )
  );
  }

  handleDrawerOpen = () => {
    this.setState({ open: true });
  };

  handleDrawerClose = () => {
    this.setState({ open: false });
  };

  updateCurrentColorHandler = (newColor) => {
    this.setState({ currentColor: newColor.hex});
  }

  addNewColorHandler = () => {
    const newColor = { 
      color: this.state.currentColor,
      name: this.state.colorName }
    this.setState({colors: [...this.state.colors, newColor], newName: ''});
  }

  paletteChangeHandler = (evt) => {
    this.setState({
      [evt.target.name]: evt.target.value
    });
  }

  savePaletteHandler = () => {
    let newName = this.state.newPaletteName;
    const newPalette = {
      paletteName: newName, 
      id: newName.toLowerCase().replace(/ /g, '-'), 
      colors: this.state.colors};
    this.props.savePalette(newPalette);
    this.props.history.push('/');
  }

  deleteColorHandler = (colorName) => {
    this.setState({
      colors: this.state.colors.filter(color => color.name !== colorName)
    });
  }

  render() {
    const { classes } = this.props;
    const { open, currentColor, colors, colorName, newPaletteName } = this.state;

    return (
      <div className={classes.root}>
        <CssBaseline />
        <AppBar
          position='fixed'
          color='default'
          className={classNames(classes.appBar, {
            [classes.appBarShift]: open
          })}
        >
          <Toolbar disableGutters={!open}>
            <IconButton
              color='inherit'
              aria-label='Open drawer'
              onClick={this.handleDrawerOpen}
              className={classNames(classes.menuButton, open && classes.hide)}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant='h6' color='inherit' noWrap>
              Create Your Own Palette
            </Typography>
            <ValidatorForm onSubmit={this.savePaletteHandler}>
            <TextValidator 
              label='Palette Name' 
              value={newPaletteName}
              name='newPaletteName' 
              onChange={this.paletteChangeHandler}
              validators={['required', 'isPaletteNameUnique']}
              errorMessages={['Enter a palette name', 'Palette name must be unique']}
              />
            <Fab variant='extended' color='primary' type='submit'>Save Palette</Fab>
            </ValidatorForm>
          </Toolbar>
        </AppBar>
        <Drawer
          className={classes.drawer}
          variant='persistent'
          anchor='left'
          open={open}
          classes={{
            paper: classes.drawerPaper
          }}
        >
          <div className={classes.drawerHeader}>
            <IconButton onClick={this.handleDrawerClose}>
              <ChevronLeftIcon />
            </IconButton>
          </div>
          <Typography variant='h4'>Design Your Palette</Typography>
          <div>
            <Fab variant='extended' color='secondary'>
              Clear Palette
            </Fab>
            <Fab variant='extended' color='primary'>
              Random Color
            </Fab>
          </div>
          <Divider />
          <ChromePicker 
            color={currentColor} 
            onChangeComplete={this.updateCurrentColorHandler}/>
          <ValidatorForm onSubmit={this.addNewColorHandler}>
            <TextValidator 
              value={colorName} 
              name='colorName'
              onChange={this.paletteChangeHandler}
              validators={['required', 'isColorUnique', 'isColorNameUnique']}
              errorMessages={['Enter a color name', 'Color is already used', 'Color name must be unique']}
              />
            <Fab 
            variant='extended'
            type='submit' 
            color='primary'
            style={{backgroundColor: currentColor}}
            >
              Add Color
            </Fab>
          </ValidatorForm>
        </Drawer>
        <main
          className={classNames(classes.content, {
            [classes.contentShift]: open
          })}
        >
          <div className={classes.drawerHeader} />
          <DraggableColourList colors={colors} deleteColorHandler={this.deleteColorHandler}/>
        </main>
      </div>
    );
  }
}

export default withStyles(styles, {withTheme: true})(NewPaletteForm);
