import React, { Component } from 'react';

import './Pokedex.css';
import Pokecard from '../Pokecard/Pokecard';

class Pokedex extends Component {
  static defaultProps = {
    pokemon: [
      {id: 'bulbasaur', name: 'Bulbasaur', type: 'grass', base_experience: 53},
      {id: 'charmander', name: 'Charmander', type: 'fire', base_experience: 62},
      {id: 'squirtle', name: 'Squirtle', type: 'water', base_experience: 63},
      {id: 'butterfree', name: 'Butterfree', type: 'flying', base_experience: 178},
      {id: 'pikachu', name: 'Pikachu', type: 'electric', base_experience: 112},
      {id: 'eevee', name: 'Eevee', type: 'normal', base_experience: 65},
      {id: 'flareon', name: 'Flareon', type: 'fire', base_experience: 95},
      {id: 'vaporeon', name: 'Vaporeon', type: 'water', base_experience: 225},

    ]
  }
  render() {
    return (
      <div className="Pokedex">
        <h1>Pokedex!</h1>
        <div className="Pokedex-cards">
          {this.props.pokemon.map((p) => (
            <Pokecard id={p.id} name={p.name} type={p.type} baseExp={p.base_experience}/>
          ))}
        </div>
      </div>
    )
  }
}

export default Pokedex;
