import React, { Component } from 'react';

import Die from './Die';

class RollDice extends Component {
  state = {
    die1: 'one',
    die2: 'two',
  }

  render(){
    return(
      <div>
        <Die face={this.state.die1}/>
        <Die face='one'/>
        <button>Click to roll!</button>
      </div>
    )
  }
}

export default RollDice;
