import React, { Component } from 'react';

import LotteryBall from './LotteryBall';

class Lottery extends Component {
  render(){
    return (
      <div>
      <h1>{this.props.title}</h1>
      <LotteryBall num={2}/>
      <button>Generate</button>
      </div>
    )
  }
}

export default Lottery;
