import React, { Component } from 'react';

import Coin from './Coin';
import { choice } from './helpers';

class CoinFlipper extends Component {
  static defaultProps = {
    coins: [
      {side: 'heads', imgSrc: 'https://tinyurl.com/react-coin-heads-jpg'},
      {side: 'tails', imgSrc: 'https://tinyurl.com/react-coin-tails-jpg'}
    ]
  }

  state = {
    currentCoin: null,
    numOfFlips: 0,
    numOfHeads: 0,
    numOfTails: 0,
  }

  flipHandler = () => {
    const newCoin = choice(this.props.coins);
    this.setState(st => {
      return {
        currentCoin: newCoin,
        numOfFlips: st.numOfFlips + 1,
      };
    })
  }

  render() {
    return (
      <div>
        <h1>Let's flip a coin!</h1>
        <Coin imgSrc={this.state.currentCoin.coins.imgSrc} name='coin'/>
        <button onClick={this.flipHandler}>FLIP MEEE!</button>
        <p>Out of {this.state.numOfFlips} flips, there have been {this.state.numOfHeads} heads and {this.state.numOfTails} tails.</p>
      </div>
    )
  }
}

export default CoinFlipper;
