import React, { Component } from 'react';

import './Box.css';

class Box extends Component {
  static defaultProps = {
    colour: [
      {colour: 'red'},
      {colour: 'steelblue'},
      {colour: 'salmon'},
    ]
  }

  state = {
    colour: 'red'
  }

  render(){
    const red = 'red';

    return (
      <div style={{backgroundColor: this.state.colour, height: '150px', width: '150px'}}></div>
    )
  }
}

export default Box;
