import React, { Component } from 'react';

import './Box.css';
import { choice } from './helpers';

class Box extends Component {
  static defaultProps = {
    colours: ['red', 'steelblue', 'salmon', 'violet', 'green', 'gold', 'orange', 'purple', 'grey', 'blue', 'skyblue', 'teal']
  }

  state = {
    colour: choice(this.props.colours)
  }

  randomiseColourHandler = () => {
    const newColour = choice(this.props.colours);
    this.setState(st => {
      return {
        colour: newColour
      }
    })
  }

  render(){
    const red = 'red';

    return (
      <div 
        style={{backgroundColor: this.state.colour, height: '150px', width: '150px'}}
        onClick={this.randomiseColourHandler}></div>
    )
  }
}

export default Box;
